mapper.select:
        select

        from	wx_user_group_relation t

			1=1
             and t.group_GUSERCODE = #{groupGusercode}
             and t.group_GUSERNAME like concat('%', #{groupGusername}, '%')
             and t.GROUP_GCODE = #{groupGcode}
             and t.GROUP_GCNAME like concat('%', #{groupGcname}, '%')
             and t.user_uaccount = #{userUaccount}
             and t.user_UNAME like concat('%', #{userUname}, '%')
             and t.DEL_FLAG = #{delFlag}
             and t.CREATE_BY = #{createBy}
             and t.CREATE_DATE = #{createDate}
             and t.UPDATE_BY = #{updateBy}
             and t.UPDATE_DATE = #{updateDate}
             and t.INST_ID = #{instId}
             and t.INST_NAME like concat('%', #{instName}, '%')
             and t.REMARKS = #{remarks}

			 and ${dynamicSQL} 
			 ORDER BY t.${sortC} ${order}

